mapper.select:
SELECT * FROM trade_request 
WHERE seller_user_id = #{userId} OR buyer_user_id = #{userId}
ORDER BY created_at DESC

SELECT * FROM trade_request 
WHERE reservation_id = #{reservationId}
ORDER BY created_at DESC

mapper.insert:
INSERT INTO trade_request (
    reservation_id, seller_user_id, buyer_user_id, price, status, created_at, updated_at
) VALUES (
    #{reservationId}, #{sellerUserId}, #{buyerUserId}, #{price},
    COALESCE(#{status}, 'PENDING'), NOW(), NOW()
)
